import React, { useState, useEffect } from "react";
import { createClient } from 'pexels';

function App() {
  const [images, setImages] = useState([]);
  const client = createClient(process.env.REACT_APP_KEY);
  const query = "cosmos";

  useEffect(() => {
    client.photos.search({ query, per_page: 6 })
      .then(photos => {
        if (photos.photos) {
          setImages(photos.photos);  

        }
      });
  }, []);  

  const imageElements = [];
  images.forEach((image, index) => {
    const element = (
      <div key={index} className="max-w-sm rounded overflow-hidden shadow-lg">
        <img src={image.src.portrait} alt="" className="w-full"/>
        <div className="px-6 py-4">
          <div className="font-bold text-purple-500 text-xl mb-2">
            Photo by {image.photographer}
          </div>
          <ul>
            <li>
              <strong>Title: </strong>
              {image.alt}
            </li>
          </ul>
        </div>
      </div>
    );
    imageElements.push(element);
  });

  return (
    <div className="container mx-auto">
      <div className="content-center grid grid-cols-3 gap-4">
        {imageElements} 
        {console.log(images)} 
      </div>
    </div>
  );
}

export default App;
